class ZodType {
  optional() {
    return new ZodOptional(this);
  }
}

class ZodString extends ZodType {
  constructor() {
    super();
    this._def = { typeName: "ZodString" };
  }
}

class ZodNumber extends ZodType {
  constructor() {
    super();
    this._def = { typeName: "ZodNumber" };
  }
}

class ZodBoolean extends ZodType {
  constructor() {
    super();
    this._def = { typeName: "ZodBoolean" };
  }
}

class ZodArray extends ZodType {
  constructor(element) {
    super();
    this._def = { typeName: "ZodArray", type: element };
  }
}

class ZodOptional extends ZodType {
  constructor(inner) {
    super();
    this._def = { typeName: "ZodOptional", innerType: inner };
  }
}

class ZodObject extends ZodType {
  constructor(shape) {
    super();
    this._def = {
      typeName: "ZodObject",
      shape: () => shape,
    };
  }
}

export const z = {
  string() {
    return new ZodString();
  },
  number() {
    return new ZodNumber();
  },
  boolean() {
    return new ZodBoolean();
  },
  array(inner) {
    return new ZodArray(inner);
  },
  object(shape) {
    return new ZodObject(shape);
  },
};
